<script>
import InitialMessageSVG from './InitialMessageSVG.vue'
import HumanWonSVG from './HumanWonSVG.vue'
import HumanLostSVG from './HumanLostSVG.vue'
import TieSVG from './TieSVG.vue'

export default {
    name: 'Header',
    components: {
        InitialMessageSVG,
        HumanWonSVG,
        HumanLostSVG,
        TieSVG,
    },
    props: {
        roundResult: {
            type: String,
            required: true
        },
        computerChoice: {
            type: String,
            required: true
        }
    },
    data() {
        return {
            chosenCircle: null,
            wasRoundPlayed: false
        }
    },
    methods: {
        // Pass humanChoice value to App by emitting 'play-round' -> App which passes back roundResult (as a prop)
        playRound(humanChoice) {
            this.chosenCircle = humanChoice
            this.wasRoundPlayed = true
            this.$emit('play-round', humanChoice)
        },
        // Assign deciding variables their initial values
        playAgain() {
            this.$emit('play-again')
            this.wasRoundPlayed = false
            this.chosenCircle = null
            return this.chosenCircle
        }
    },
}
</script>

